INSERT INTO `theme` (`id`, `name`, `description`, `color`, `css`, `created_by`, `updated_by`, `created_at`, `updated_at`)
VALUES
	(1,'Blue Denim','Dark blue body with white big fields.','#212a3e','\nbody {\n    background-color: #212a3e;\n    padding: 20px;\n    color: #7b8291;\n}\n\n/* Small devices (tablets, 768px and up) */\n@media (min-width: 768px) {\n    body {\n        padding: 50px;\n    }\n}\nstrong {\n    color: #FFFFFF;\n}\n.legend {\n    font-family: \"helvetica\", \"arial\", \"sans-serif\";\n    font-size: 28px;\n    font-weight: 400;\n    line-height: 1.4;\n    color: #fff;\n    margin: 0 0 5px;\n}\np.description {\n    color: #1ec185;\n    background: url(\"data:image/png;base64,\") 0 40% no-repeat;\n    font-size: 14px;\n    padding-left: 20px;\n    display: inline-block;\n    margin: 5px auto 0;\n}\n.form-group, .form-action {\n    font-size: 14px;\n    color: #7b8291;\n    margin: 27px 0 9px;\n}\n.control-label {\n    font-size: 16px;\n    color: #fff;\n    margin: 0 5px 9px 0;\n}\n.required-control .control-label:after {\n    color: #e55;\n    margin-left: 5px;\n}\n.form-control {\n    background: #363e51;\n    border-color: transparent;\n    border-radius: 3px;\n    color: #fff;\n    margin: 0;\n    height: 36px;\n    width: 100%;\n    -webkit-transition: background .08s linear;\n    -moz-transition: background .08s linear;\n    -o-transition: background .08s linear;\n    transition: background .08s linear;\n}\n.form-control:hover {\n    background: #424a5b\n}\n.form-control:focus {\n    background: #fff;\n    color: #212a3e\n}\n.form-control::-webkit-input-placeholder {\n    color: #7b8291\n}\n.form-control::-moz-placeholder {\n    color: #7b8291\n}\n.form-control:-ms-input-placeholder {\n    color: #7b8291\n}\n.form-control::placeholder {\n    color: #7b8291\n}\n.btn {\n    border-radius: 4px;\n    border: 0 !important;\n    font-size: 18px;\n    font-weight: 500;\n    width: auto;\n    height: 55px;\n    line-height: 55px;\n    margin: 0;\n    padding: 0 30px;\n    -webkit-transition: background-color .1s ease;\n    -moz-transition: background-color .1s ease;\n    -o-transition: background-color .1s ease;\n    transition: background-color .1s ease;\n}\n.btn-primary {\n    background-color: #1ec185;\n}\n.btn-primary:focus, .btn-primary:active, .btn-primary:hover {\n    background-color: #1baf79 !important;\n    color: #fff;\n    border: 0 !important;\n    outline: 0 none !important;\n    box-shadow: none !important;\n}\n',1,1,1497990040,1497990040),
	(2,'Sky Blue','Blue sky background with clouds around. White form with a thin typography.','#95D6FE','\n@import url(https://fonts.googleapis.com/css?family=Roboto:400,300);\nbody {\n    background-color: #95D6FE;\n    overflow-x: hidden;\n    padding: 20px;\n    color: #A9A9A9;\n    color: rgba(255, 255, 255, 0.6);\n    font-family: Roboto, sans-serif;\n}\n/* Small devices (tablets, 768px and up) */\n@media (min-width: 768px) {\n    body {\n        padding: 50px 25%;\n    }\n}\n/**\n * Form\n */\nh3 {\n    color: #FFFFFF;\n    font-size: 32px;\n    font-weight: 300;\n    text-align: center;\n    margin: .3em 0;\n    -webkit-animation: titleFadein .8s ease;\n    -moz-animation: titleFadein .8s ease;\n    animation: titleFadein .8s ease;\n}\np:first-of-type {\n    color: #FFFFFF;\n    color: rgba(255, 255, 255, 0.92);\n    font-weight: 300;\n    font-size: 20px;\n    text-shadow: none;\n    text-align: center;\n}\n.form-group {\n    background-color: #ffffff;\n    font-size: 14px;\n    padding: 20px 40px 10px;\n    margin: 0;\n}\n.form-group:first-of-type {\n    padding-top: 40px;\n    margin-top: 40px;\n    border-radius: 12px 12px 0 0;\n}\n.form-action {\n    margin: 0;\n    padding: 20px 40px 40px 40px;\n    border-radius: 0 0 12px 12px;\n    background-color: #ffffff;\n    font-size: 14px;\n}\n.control-label {\n    font-weight: 300;\n    font-size: 16px;\n    color: #777777;\n}\n.form-control {\n    width: 100%;\n    height: 48px;\n    -webkit-box-sizing: padding-box;\n    -moz-box-sizing: padding-box;\n    box-sizing: padding-box;\n    -webkit-appearance: none;\n    -moz-appearance: none;\n    appearance: none;\n    background-color: #EFEFEF;\n    background-color: rgba(0, 0, 0, 0.03);\n    border-color: transparent;\n    border-radius: 6px;\n    font-size: 20px;\n    font-family: Roboto, sans-serif;\n    font-weight: 300;\n    box-shadow: inset 0px 2px 1px rgba(0, 0, 0, .03);\n    outline: none;\n    text-overflow: ellipsis;\n    -webkit-font-smoothing: antialiased;\n}\n.form-control:focus {\n    border-color: rgb(138, 197, 65);\n    box-shadow: inset 0 0 0 0,inset 0 1px 2px rgba(138, 197, 65, 0.15),0 0 10px rgba(138, 197, 65, 0.8),0 2px 0 rgba(138, 197, 65,0.1);\n    transition: none;\n}\n.form-control::-webkit-input-placeholder {\n    color: #A9A9A9;\n}\n.form-control::-moz-placeholder {\n    color: #A9A9A9;\n}\n.form-control:-ms-input-placeholder {\n    color: #A9A9A9;\n}\n.form-control::placeholder {\n    color: #A9A9A9;\n}\n.radio label, .checkbox label {\n    color: #7C7C7C;\n    font-weight: 300;\n}\n.btn {\n    display: block;\n    padding: 0 28px;\n    border-radius: 28px;\n    width: auto;\n    height: 56px;\n    margin: 0 auto;\n    overflow: hidden;\n    -webkit-font-smoothing: antialiased;\n    font-size: 24px;\n}\n.btn-primary {\n    background-color: rgb(138, 197, 65);\n    background-color: rgba(138, 197, 65, 0.90);\n    border-color: transparent;\n}\n.btn-primary:focus, .btn-primary:hover {\n    background: rgb(138, 197, 65) !important;\n    border-color: transparent !important;\n    color: rgb(255, 255, 255) !important;\n}\n.info {\n    font-size: 16px;\n    color: rgb(102, 102, 102);\n    color: rgba(102, 102, 102, 0.3);\n    padding: 10px 40px;\n    background-color: #ffffff;\n    margin: 0;\n}\n/**\n * Clouds\n *\n * You must add this snippet to your form:\n *\n * <div id=\"clouds\">\n *   <div class=\"cloud x1\"></div>\n *   <div class=\"cloud x2\"></div>\n *   <div class=\"cloud x3\"></div>\n *   <div class=\"cloud x4\"></div>\n *   <div class=\"cloud x5\"></div>\n * </div>\n *\n * Based on http://thecodeplayer.com/walkthrough/pure-css3-animated-clouds-background\n */\n#clouds{\n    top: 220px;\n    padding: 100px 0;\n    position: absolute;\n    z-index: -1;\n}\n/*Time to finalise the cloud shape*/\n.cloud {\n    width: 200px; height: 60px;\n    background: #fff;\n\n    border-radius: 200px;\n    -moz-border-radius: 200px;\n    -webkit-border-radius: 200px;\n\n    position: relative;\n}\n.cloud:bef